import React, { useRef, useEffect, useState } from 'react';

const Annotation = () => {
  const circleRef = useRef(null);
  const [intervalId, setIntervalId] = useState(null);
  const positionRef = useRef({ x: 0, y: 0 });

  const updateCoordinates = () => {
    const circle = circleRef.current;
    const rect = circle.getBoundingClientRect();
    const circleCenterX = rect.left + rect.width / 2;
    const circleCenterY = rect.top + rect.height / 2;

    const { x, y } = positionRef.current;

    const relativeX = x - circleCenterX;
    const relativeY = y - circleCenterY;

    console.log(`X: ${relativeX}, Y: ${relativeY}`);
  };

  const handleMove = (event) => {
    const moveX = event.clientX || event.touches[0].clientX;
    const moveY = event.clientY || event.touches[0].clientY;

    positionRef.current = { x: moveX, y: moveY };
  };

  const handleMouseDown = () => {
    document.addEventListener('mousemove', handleMove);
    document.addEventListener('mouseup', handleMouseUp);

    const id = setInterval(updateCoordinates, 100);
    setIntervalId(id);
  };

  const handleMouseUp = () => {
    document.removeEventListener('mousemove', handleMove);
    document.removeEventListener('mouseup', handleMouseUp);

    if (intervalId) {
      clearInterval(intervalId);
      setIntervalId(null);
    }
  };

  const handleTouchStart = () => {
    document.addEventListener('touchmove', handleMove);
    document.addEventListener('touchend', handleTouchEnd);

    const id = setInterval(updateCoordinates, 100);
    setIntervalId(id);
  };

  const handleTouchEnd = () => {
    document.removeEventListener('touchmove', handleMove);
    document.removeEventListener('touchend', handleTouchEnd);

    if (intervalId) {
      clearInterval(intervalId);
      setIntervalId(null);
    }
  };

  useEffect(() => {
    return () => {
      document.removeEventListener('mousemove', handleMove);
      document.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('touchmove', handleMove);
      document.removeEventListener('touchend', handleTouchEnd);

      if (intervalId) {
        clearInterval(intervalId);
      }
    };
  }, [intervalId]);

  return (
    <div
      ref={circleRef}
      onMouseDown={handleMouseDown}
      onTouchStart={handleTouchStart}
      style={{
        width: '200px',
        height: '200px',
        borderRadius: '50%',
        backgroundColor: 'blue',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'relative',
        margin: '100px auto',
        touchAction: 'none', // Prevent default touch action to ensure smooth handling
      }}
    >
      <span style={{ color: 'white', fontWeight: 'bold' }}>Click Me</span>
    </div>
  );
};

export default Annotation;
