// src/App.js
import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Annotation from './components/Annotation';
import SignUp from './components/SignUp';
import List from './components/List'; // Import the List component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState('');
  const [isSignUp, setIsSignUp] = useState(false);
  const [email, setEmail] = useState('');
  const [selectedRecordingId, setSelectedRecordingId] = useState(null); // State to manage selected recording ID
  const [recordings] = useState([
    { id: 1 },
    { id: 2 },
    { id: 3 },
    { id: 4 },
    { id: 5 }
  ]); // Sample recordings data

  const handleLogin = (email, authToken) => {
    setToken(authToken);
    setEmail(email);
    setIsLoggedIn(true);
  };

  const handleSignUp = () => {
    setIsSignUp(true);
  };

  const handleSelectRecording = (id) => {
    setSelectedRecordingId(id);
  };

  return (
    <div className="App">
      <header className="App-header">
        <Router>
          <Routes>
            <Route 
              path="/login" 
              element={isLoggedIn ? <Navigate to="/list" /> : <Login onLogin={handleLogin} onSignUp={handleSignUp} />} 
            />
            <Route 
              path="/signup" 
              element={isSignUp ? <SignUp onSignUp={handleLogin} /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/list" 
              element={isLoggedIn ? <List recordings={recordings} onSelectRecording={handleSelectRecording} /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/annotation" 
              element={isLoggedIn && selectedRecordingId !== null ? (
                <Annotation token={token} userId={email} recordingId={selectedRecordingId} />
              ) : (
                <Navigate to={selectedRecordingId === null ? "/list" : "/login"} />
              )} 
            />
            <Route 
              path="*" 
              element={<Navigate to={isLoggedIn ? "/list" : "/login"} />} 
            />
          </Routes>
        </Router>
      </header>
    </div>
  );
}

export default App;
