// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import Annotation from './components/Annotation';
import SignUp from './components/SignUp';
import List from './components/List';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState('');
  const [email, setEmail] = useState('');
  const [selectedRecordingId, setSelectedRecordingId] = useState(null);

  const handleLogin = (email, authToken) => {
    setToken(authToken);
    setEmail(email);
    setIsLoggedIn(true);
  };

  const handleSignUp = () => {
    setIsLoggedIn(true);
  };

  const handleSelectRecording = (id) => {
    setSelectedRecordingId(id);
  };

  return (
    <Router>
      <Routes>
        {/* Redirect to login if not logged in */}
        <Route path="/" element={isLoggedIn ? <Navigate to="/list" /> : <Navigate to="/login" />} />

        {/* Login page */}
        <Route path="/login" element={<Login onLogin={handleLogin} />} />

        {/* Sign-up page */}
        <Route path="/signup" element={<SignUp onSignUp={handleSignUp} />} />

        {/* List page, visible after login */}
        <Route
          path="/list"
          element={isLoggedIn ? <List recordings={[{ id: 1 }, { id: 2 }, { id: 3 }]} onSelectRecording={handleSelectRecording} /> : <Navigate to="/login" />}
        />

        {/* Annotation page for a specific recording */}
        <Route
          path="/annotation"
          element={selectedRecordingId !== null && isLoggedIn ? (
            <Annotation token={token} userId={email} recordingId={selectedRecordingId} />
          ) : (
            <Navigate to="/list" />
          )}
        />
      </Routes>
    </Router>
  );
}

export default App;
