import React, { useState } from 'react';
import './App.css';
import Login from './components/Login';
import Annotation from './components/Annotation';
import SignUp from './components/SignUp'; // Import the SignUp component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState(''); // State to store authentication token
  const [isSignUp, setIsSignUp] = useState(false); // State to manage sign-up option

  const handleLogin = (authToken) => {
    setToken(authToken); // Save the token
    setIsLoggedIn(true); // Update login state
  };

  const handleSignUp = () => {
    setIsSignUp(true); // Switch to sign-up mode
  };

  return (
    <div className="App">
      <header className="App-header">
        {isLoggedIn ? (
          <Annotation token={token} /> // Render Annotation component if logged in
        ) : isSignUp ? (
          <SignUp onSignUp={handleLogin} /> // Render SignUp component if signing up
        ) : (
          <Login onLogin={handleLogin} onSignUp={handleSignUp} /> // Render Login component otherwise
        )}
      </header>
    </div>
  );
}

export default App;
