import React from 'react';
import './EmotionModal.css';

const emotionStyles = {
  happiness: { backgroundColor: '#FFD700', color: '#000000' },  // Gold
  sadness: { backgroundColor: '#1E90FF', color: '#FFFFFF' },  // Dodger Blue
  fear: { backgroundColor: '#8B0000', color: '#FFFFFF' },      // Dark Red
  disgust: { backgroundColor: '#228B22', color: '#FFFFFF' },   // Forest Green
  anger: { backgroundColor: '#FF4500', color: '#FFFFFF' },     // Orange Red
  surprise: { backgroundColor: '#DA70D6', color: '#000000' }    // Orchid
};

const EmotionModal = ({ isOpen, onClose, onConfirm }) => {
  const handleEmotionSelect = (emotion) => {
    onConfirm(emotion);  // Invoke the onConfirm callback when an emotion is selected
    onClose();  // Close the modal
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="emotion-options">
          {Object.keys(emotionStyles).map((emotion) => (
            <button
              key={emotion}
              onClick={() => handleEmotionSelect(emotion)}
              className="emotion-button"
              style={emotionStyles[emotion]}
            >
              {emotion.charAt(0).toUpperCase() + emotion.slice(1)}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EmotionModal;
