import React, { useRef, useState } from 'react';
import './Annotation.css';

const Annotation = () => {
  const circleRef = useRef(null);
  const [isMouseInside, setIsMouseInside] = useState(false);
  const [dotPosition, setDotPosition] = useState({ x: 100, y: 100 });

  const handleMove = (event) => {
    const moveX = event.clientX || (event.touches && event.touches[0].clientX);
    const moveY = event.clientY || (event.touches && event.touches[0].clientY);

    const circle = circleRef.current;
    if (circle) {
      const rect = circle.getBoundingClientRect();
      const relativeX = moveX - rect.left;
      const relativeY = moveY - rect.top;

      setDotPosition({ x: relativeX, y: relativeY });
      setIsMouseInside(
        relativeX >= 0 &&
        relativeX <= rect.width &&
        relativeY >= 0 &&
        relativeY <= rect.height
      );
    }
  };

  const handleMouseDown = (event) => {
    handleMove(event); // Register initial click coordinates
    document.addEventListener('mousemove', handleMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleMouseUp = () => {
    document.removeEventListener('mousemove', handleMove);
    document.removeEventListener('mouseup', handleMouseUp);
  };

  const handleTouchStart = (event) => {
    handleMove(event); // Register initial touch coordinates
    document.addEventListener('touchmove', handleMove);
    document.addEventListener('touchend', handleTouchEnd);
  };

  const handleTouchEnd = () => {
    document.removeEventListener('touchmove', handleMove);
    document.removeEventListener('touchend', handleTouchEnd);
  };

  return (
    <div className="circle-container">
      <div
        ref={circleRef}
        onMouseDown={handleMouseDown}
        onTouchStart={handleTouchStart}
        className="circle"
        title="Annotation Circle"
        style={{ left: '100px', top: '100px' }} // Set initial position here
      >
        {isMouseInside && (
          <div
            className="dot"
            style={{ left: `${dotPosition.x}px`, top: `${dotPosition.y}px` }}
          ></div>
        )}
        <span>Click Me</span>
      </div>
    </div>
  );
};

export default Annotation;
