import React, { useState } from 'react';
import './EmotionModal.css';

const EmotionModal = ({ isOpen, onClose, onConfirm }) => {
  const [selectedEmotion, setSelectedEmotion] = useState('');

  const handleEmotionSelect = (emotion) => {
    setSelectedEmotion(emotion);
  };

  const handleConfirm = () => {
    onConfirm(selectedEmotion);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Select an Emotion</h2>
        <div className="emotion-options">
          {['happiness', 'sadness', 'fear', 'disgust', 'anger', 'surprise'].map((emotion) => (
            <button
              key={emotion}
              onClick={() => handleEmotionSelect(emotion)}
              className="emotion-button"
            >
              {emotion.charAt(0).toUpperCase() + emotion.slice(1)}
            </button>
          ))}
        </div>
        <button onClick={handleConfirm} disabled={!selectedEmotion} className="confirm-button">
          Confirm
        </button>
        <button onClick={onClose} className="cancel-button">
          Cancel
        </button>
      </div>
    </div>
  );
};

export default EmotionModal;
