import React, { useState } from 'react';
import './EmotionModal.css';

const emotionStyles = {
  happiness: { backgroundColor: '#FFFF00', color: '#000000' },
  sadness: { backgroundColor: '#1E90FF', color: '#FFFFFF' },
  fear: { backgroundColor: '#000000', color: '#FFFFFF' },
  disgust: { backgroundColor: '#008000', color: '#FFFFFF' },
  anger: { backgroundColor: '#FF0000', color: '#FFFFFF' },
  surprise: { backgroundColor: '#FFA500', color: '#000000' },
  neutral: { backgroundColor: '#808080', color: '#FFFFFF' }
};

const EmotionModal = ({ isOpen, onClose, onConfirm }) => {
  const [familiarity, setFamiliarity] = useState('');
  const [liking, setLiking] = useState('');
  const [selectedEmotion, setSelectedEmotion] = useState('');

  const handleEmotionSelect = (emotion) => {
    setSelectedEmotion(emotion);
  };

  const handleFinish = () => {
    if (familiarity && liking && selectedEmotion) {
      onConfirm({ familiarity, liking, emotion: selectedEmotion });
      onClose();
    } else {
      alert('Please complete all fields before finishing.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div>
          <label>How familiar are you with the music track?</label>
          <input
            type="number"
            min="1"
            max="5"
            value={familiarity}
            onChange={(e) => setFamiliarity(e.target.value)}
          />
        </div>
        <div>
          <label>How much did you like it?</label>
          <input
            type="number"
            min="1"
            max="5"
            value={liking}
            onChange={(e) => setLiking(e.target.value)}
          />
        </div>
        <div className="emotion-options">
          {Object.keys(emotionStyles).map((emotion) => (
            <button
              key={emotion}
              onClick={() => handleEmotionSelect(emotion)}
              className="emotion-button"
              style={emotionStyles[emotion]}
            >
              {emotion.charAt(0).toUpperCase() + emotion.slice(1)}
            </button>
          ))}
        </div>
        <button onClick={handleFinish} className="finish-button">
          Finish and Download
        </button>
      </div>
    </div>
  );
};

export default EmotionModal;
