import React, { useRef, useEffect, useState } from 'react';
import './Annotation.css';

const Annotation = ({ userId, sessionId, token }) => {
  const circleRef = useRef(null);
  const dotRef = useRef(null);
  const [isTracking, setIsTracking] = useState(false);
  const [annotationInterval, setAnnotationInterval] = useState(null);
  const [annotations, setAnnotations] = useState([]);

  const saveAnnotationLocally = (annotation) => {
    const existingAnnotations = JSON.parse(localStorage.getItem('annotations')) || [];
    existingAnnotations.push(annotation);
    localStorage.setItem('annotations', JSON.stringify(existingAnnotations));
    setAnnotations(existingAnnotations);
  };

  const sendAnnotation = async (relativeX, relativeY) => {
    try {
      const annotationTime = new Date().toISOString();
      const annotation = {
        arousal: Math.round(relativeX),
        valence: Math.round(relativeY),
        user_id: userId,
        session_id: sessionId,
        annotation_time: annotationTime,
      };

      console.log('Sending annotation:', annotation);

      // Save the annotation locally
      saveAnnotationLocally(annotation);

      // Save annotation to the server
      const response = await fetch('https://x8ki-letl-twmt.n7.xano.io/api:w7sbvKhS/annotations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(annotation),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Annotation saved:', data);
      } else {
        console.error('Failed to save annotation');
      }
    } catch (error) {
      console.error('Error saving annotation:', error);
    }
  };

  const updateCoordinates = () => {
    const circle = circleRef.current;
    const dot = dotRef.current;

    if (circle && dot) {
      const rect = circle.getBoundingClientRect();
      const centerX = rect.left + rect.width / 2;
      const centerY = rect.top + rect.height / 2;

      const dotRect = dot.getBoundingClientRect();
      const moveX = dotRect.left + dotRect.width / 2;
      const moveY = dotRect.top + dotRect.height / 2;

      const relativeX = moveX - centerX;
      const relativeY = moveY - centerY;

      sendAnnotation(relativeX, -relativeY);
    }
  };

  const handleMove = (event) => {
    const moveX = event.clientX || (event.touches && event.touches[0].clientX);
    const moveY = event.clientY || (event.touches && event.touches[0].clientY);

    const circle = circleRef.current;
    const dot = dotRef.current;
    if (circle && dot) {
      const rect = circle.getBoundingClientRect();
      const centerX = rect.left + rect.width / 2;
      const centerY = rect.top + rect.height / 2;

      const relativeX = moveX - centerX;
      const relativeY = moveY - centerY;

      const distanceFromCenter = Math.sqrt(
        Math.pow(relativeX, 2) + Math.pow(relativeY, 2)
      );
      const isInsideCircle = distanceFromCenter <= rect.width / 2;

      if (isInsideCircle) {
        dot.style.left = `${moveX}px`;
        dot.style.top = `${moveY}px`;
      }
    }
  };

  const handleMouseDown = (event) => {
    setIsTracking(true);
    handleMove(event);
    document.addEventListener('mousemove', handleMove);
    document.addEventListener('mouseup', handleMouseUp);

    if (!annotationInterval) {
      const intervalId = setInterval(updateCoordinates, 2500);
      setAnnotationInterval(intervalId);
    }
  };

  const handleMouseUp = () => {
    setIsTracking(false);
    document.removeEventListener('mousemove', handleMove);
    document.removeEventListener('mouseup', handleMouseUp);

    if (annotationInterval) {
      clearInterval(annotationInterval);
      setAnnotationInterval(null);
    }
  };

  const handleTouchStart = (event) => {
    setIsTracking(true);
    handleMove(event);
    document.addEventListener('touchmove', handleMove);
    document.addEventListener('touchend', handleTouchEnd);

    if (!annotationInterval) {
      const intervalId = setInterval(updateCoordinates, 2500);
      setAnnotationInterval(intervalId);
    }
  };

  const handleTouchEnd = () => {
    setIsTracking(false);
    document.removeEventListener('touchmove', handleMove);
    document.removeEventListener('touchend', handleTouchEnd);

    if (annotationInterval) {
      clearInterval(annotationInterval);
      setAnnotationInterval(null);
    }
  };

  const downloadAnnotations = () => {
    const dataStr = JSON.stringify(annotations, null, 2);
    const blob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'annotations.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  useEffect(() => {
    // Load existing annotations from localStorage when component mounts
    const existingAnnotations = JSON.parse(localStorage.getItem('annotations')) || [];
    setAnnotations(existingAnnotations);
    console.log('Loaded annotations:', existingAnnotations);

    return () => {
      document.removeEventListener('mousemove', handleMove);
      document.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('touchmove', handleMove);
      document.removeEventListener('touchend', handleTouchEnd);
      if (annotationInterval) {
        clearInterval(annotationInterval);
      }
    };
  }, []);

  return (
    <div className={`circle-container ${isTracking ? 'tracking' : ''}`}>
      <div
        ref={circleRef}
        onMouseDown={handleMouseDown}
        onTouchStart={handleTouchStart}
        className="circle"
        title="Annotation Circle"
      >
      </div>
      <div ref={dotRef} className="dot"></div>
      <button onClick={downloadAnnotations}>Download Annotations</button>
    </div>
  );
};

export default Annotation;
