import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import List from './components/List';
import Annotation from './components/Annotation';
import SignUp from './components/SignUp';
import Login from './components/Login';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [selectedRecordingId, setSelectedRecordingId] = React.useState(null);
  const [recordings, setRecordings] = React.useState([]);
  const [isSignUp, setIsSignUp] = React.useState(false);
  const [token, setToken] = React.useState('');
  const [email, setEmail] = React.useState('');

  const handleLogin = (email, token) => {
    setEmail(email);
    setToken(token);
    setIsLoggedIn(true);
  };

  const handleSignUp = () => {
    setIsSignUp(true);
  };

  const handleSelectRecording = (recordingId) => {
    setSelectedRecordingId(recordingId);
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          isLoggedIn ? (
            selectedRecordingId !== null ? (
              <Navigate to="/annotation" replace />
            ) : (
              <List recordings={recordings} onSelectRecording={handleSelectRecording} />
            )
          ) : isSignUp ? (
            <SignUp onSignUp={handleLogin} />
          ) : (
            <Login onLogin={handleLogin} onSignUp={handleSignUp} />
          )
        }
      />
      <Route
        path="/annotation"
        element={
          isLoggedIn && selectedRecordingId !== null ? (
            <Annotation token={token} userId={email} recordingId={selectedRecordingId} />
          ) : (
            <Navigate to="/" replace />
          )
        }
      />
    </Routes>
  );
};

export default App;
