import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import Login from './components/Login';
import SignUp from './components/SignUp';
import List from './components/List';
import Annotation from './components/Annotation';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);
  const [selectedRecordingId, setSelectedRecordingId] = useState(null);
  const [token, setToken] = useState('');
  const [email, setEmail] = useState('');
  const [recordings, setRecordings] = useState([]);

  const navigate = useNavigate();

  const handleLogin = (token, email) => {
    setIsLoggedIn(true);
    setToken(token);
    setEmail(email);
  };

  const handleSignUp = () => {
    setIsSignUp(true);
  };

  const handleSelectRecording = (id) => {
    setSelectedRecordingId(id);
    navigate('/annotation'); // Navigate to the annotation page
  };

  return (
    <div className="App">
      <header className="App-header">
        <Routes>
          <Route
            path="/"
            element={
              isLoggedIn ? (
                selectedRecordingId !== null ? (
                  <Navigate to="/annotation" replace />
                ) : (
                  <List recordings={recordings} onSelectRecording={handleSelectRecording} />
                )
              ) : isSignUp ? (
                <SignUp onSignUp={handleLogin} />
              ) : (
                <Login onLogin={handleLogin} onSignUp={handleSignUp} />
              )
            }
          />
          <Route
            path="/annotation"
            element={
              isLoggedIn && selectedRecordingId !== null ? (
                <Annotation token={token} userId={email} recordingId={selectedRecordingId} />
              ) : (
                <Navigate to="/" replace />
              )
            }
          />
        </Routes>
      </header>
    </div>
  );
}

function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}

export default AppWrapper;
