import React, { useRef, useEffect, useState } from 'react';

const Annotation = () => {
  const circleRef = useRef(null);
  const [isMouseDown, setIsMouseDown] = useState(false);
  const [lastX, setLastX] = useState(null);
  const [lastY, setLastY] = useState(null);

  const logCoordinates = (event) => {
    const circle = circleRef.current;
    const rect = circle.getBoundingClientRect();
    const circleCenterX = rect.left + rect.width / 2;
    const circleCenterY = rect.top + rect.height / 2;

    const moveX = event.clientX || (event.touches && event.touches[0].clientX);
    const moveY = event.clientY || (event.touches && event.touches[0].clientY);

    const relativeX = moveX - circleCenterX;
    const relativeY = moveY - circleCenterY;

    console.log(`X: ${relativeX}, Y: ${relativeY}`);
    setLastX(relativeX);
    setLastY(relativeY);
  };

  const handleMouseDown = (event) => {
    setIsMouseDown(true);
    logCoordinates(event);
  };

  const handleMouseMove = (event) => {
    if (isMouseDown) {
      logCoordinates(event);
    }
  };

  const handleMouseUp = () => {
    setIsMouseDown(false);
  };

  const handleTouchStart = (event) => {
    setIsMouseDown(true);
    logCoordinates(event);
  };

  const handleTouchMove = (event) => {
    if (isMouseDown) {
      logCoordinates(event);
    }
  };

  const handleTouchEnd = () => {
    setIsMouseDown(false);
  };

  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
    document.addEventListener('touchmove', handleTouchMove);
    document.addEventListener('touchend', handleTouchEnd);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('touchmove', handleTouchMove);
      document.removeEventListener('touchend', handleTouchEnd);
    };
  }, []);

  return (
    <div
      ref={circleRef}
      onMouseDown={handleMouseDown}
      onTouchStart={handleTouchStart}
      style={{
        width: '200px',
        height: '200px',
        borderRadius: '50%',
        backgroundColor: 'blue',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'relative',
        margin: '100px auto',
        touchAction: 'none', // Prevent default touch action to ensure smooth handling
      }}
    >
      <span style={{ color: 'white', fontWeight: 'bold' }}>Click Me</span>
      {isMouseDown && (
        <div style={{ position: 'absolute', left: lastX, top: lastY, width: '5px', height: '5px', backgroundColor: 'red' }} />
      )}
    </div>
  );
};

export default Annotation;
